package com.spring.controller;import com.spring.database.entity.ServiceInfo;import com.spring.repository.ServiceInfoRepository;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import javax.annotation.Resource;import javax.servlet.http.HttpServletRequest;import static org.springframework.web.bind.annotation.RequestMethod.GET;import static org.springframework.web.bind.annotation.RequestMethod.POST;/** * Created by Alia on 2017/5/10. */@Controller@RequestMapping("/serviceInfo")public class ServiceInfoController {    @Resource    private ServiceInfoRepository serviceInfoRepository;    @RequestMapping( method = GET)    public String toHome(HttpServletRequest request, Model model) {        int serviceId = Integer.parseInt(request.getParameter("id"));        ServiceInfo serviceInfo = serviceInfoRepository.getServiceInfo(serviceId);        model.addAttribute("serviceInfo", serviceInfo);        return "serviceInfoPage";    }    @RequestMapping(value = "/entity/{id}", method = GET)    public @ResponseBody    ServiceInfo getEntity(@PathVariable int id) {        ServiceInfo serviceInfo = serviceInfoRepository.getServiceInfo(id);        return serviceInfo;        /*model.addAttribute("serviceInfo", serviceInfo);        return "serviceInfoPage";*/    }    @RequestMapping(value = "/addServiceMock", method = POST)    public String doAddServiceMock(@RequestBody ServiceInfo serviceInfo, HttpServletRequest request) {        return serviceInfo.toString();    }}