package com.spring.controller;import com.spring.database.entity.ServiceInfo;import com.spring.repository.ServiceInfoRepository;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import javax.annotation.Resource;import javax.servlet.http.HttpServletRequest;import static org.springframework.web.bind.annotation.RequestMethod.POST;/** * Created by Alia on 2017/5/15. */@Controller@RequestMapping("/mock")public class ServiceMockController {    @Resource    private ServiceInfoRepository serviceInfoRepository;    @RequestMapping(value = "/addServiceMock", method = POST)    public String doAddServiceMock(@RequestBody ServiceInfo serviceInfo, HttpServletRequest request) {        return serviceInfo.toString();    }}