package com.spring.repository;import com.spring.database.entity.ServiceInfo;import java.util.List;/** * Created by Alia on 2017/5/10. */public interface ServiceInfoRepository {    /**     * 根据ID，判断Service信息是否存在*/    public boolean isExist(int id);    /**     * 根据Entity信息，判断Service信息是否存在*/    public boolean isExist(ServiceInfo serviceInfo);    /**     * 添加Service信息     * 数据库更新，返回1     * 没有更新，返回0     * */    public int addServiceInfo(ServiceInfo serviceInfo);    /**     * 查询数据库中所有Service信息     * 数据库更新，返回1     * 没有更新，返回0     * */    public List<ServiceInfo> getAllServiceInfo();    /**     * 通过ID，查询Service信息     * 数据库更新，返回1     * 没有更新，返回0*/    public ServiceInfo getServiceInfo(int id);    /**     * 通过interface_name,version，查询Service信息     * 数据库更新，返回1     * 没有更新，返回0     * */    public ServiceInfo getServiceInfo(ServiceInfo serviceInfo);    /**     * 根据ID，删除Service信息     * 数据库更新，返回1     * 没有更新，返回0     * */    public int deleteServiceInfo(int id);    /**     * 通过interface_name,version，删除Service信息     * 数据库更新，返回1     * 没有更新，返回0     * */    public int deleteServiceInfo(ServiceInfo serviceInfo);    /**     * 根据ID，更新Service信息     * 数据库更新，返回1     * 没有更新，返回0     * */    public int updateServiceInfo(int id ,ServiceInfo des);    /**     * 通过interface_name,version，更新Service信息     * 数据库更新，返回1     * 没有更新，返回0     * */    public int updateServiceInfo(ServiceInfo src,ServiceInfo des);}