package com.spring.ControllerTest;import com.spring.controller.HomeController;import org.junit.Before;import org.junit.Test;import org.mockito.InjectMocks;import org.mockito.MockitoAnnotations;import org.springframework.test.web.servlet.MockMvc;import static org.springframework.test.web.servlet.request.MockMvcRequestBuilders.get;import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.view;import static org.springframework.test.web.servlet.setup.MockMvcBuilders.standaloneSetup;/** * Created by Alia on 2017/5/10. */public class HomeControllerTest {    private MockMvc mockMvc;    @InjectMocks    private HomeController controller;    @Before    public void setup() {        MockitoAnnotations.initMocks(this);        this.mockMvc = standaloneSetup(controller).build();    }    @Test    public void homepageTest() throws Exception {        mockMvc.perform(get("/")).andExpect(view().name("homePage"));        mockMvc.perform(get("/home")).andExpect(view().name("homePage"));    }}